            Game_Canvas[i] = 0;

        if (Sequence_Step <= 9)
            menuAndMap();
        else if (Sequence_Step <= 49)
            PvEscreens();
        else if (Sequence_Step <= 59)
            townScreens();
        else if (Sequence_Step <= 60)
            PvPscreens();

