        Large_Text.TXoutputB(L,T+88,"EXP "+Team_EXP+"("+floor(100*(Team_EXP-xp_for_prev_LV)/(xp_for_next_LV-xp_for_prev_LV))+"%)",0xFFFFFF,0x000000);
        Large_Text.TXoutputB(L,T+100,"$$$ "+Team_Gold,0xFFFFFF,0x000000);
