// hides slots
function restrictSlots(item_pos,compo_slot){ // original name: Ng()
    var held_item_eff_ID = getVal(Item_Inv[Inv_Last],Eff_ID);
    var held_item_class_ID = getVal(Item_Inv[Inv_Last],Item_Class_ID);
    var weapon = Stickmen_Slots+item_pos;
    if (1<=held_item_class_ID && held_item_class_ID<=8 && held_item_class_ID!=Ranger_Class[item_pos])
        return false; // if item is a weapon AND item doesn't fit character's class

    if (Item_Inv[Inv_Last]==0 || held_item_class_ID!=Class_Compo)
        return true;  // if no item is held OR if item held is not a compo

    if (Item_Inv[weapon]==0 || (compo_slot==0 && Comp1_Inv[weapon]==Null_Slot) || (compo_slot==1 && Comp2_Inv[weapon]==Null_Slot))
        return false; // if no weapon is equipped OR if compo slot of weapon is null (store-bought weapons)

    if ((compo_slot==0 && held_item_eff_ID==getVal(Comp2_Inv[weapon],Eff_ID)) || (compo_slot==1 && held_item_eff_ID==getVal(Comp1_Inv[weapon],Eff_ID)))
        return false; // if weapon already has held compo
    else              // else check for restrictions
        return compRestrCheck(held_item_eff_ID,getVal(Item_Inv[weapon],Item_Class_ID),getVal(Item_Inv[weapon],Item_Type),getVal(Item_Inv[weapon],Item_Bullet));
}

// weapon and class restrictions on compos
function compRestrCheck(cp_eff_ID,class_ID,weap_type,weap_bullet){ // original name: Oe()
    if (cp_eff_ID==Stone_White || cp_eff_ID==Stone_Red || cp_eff_ID==Stone_Green || cp_eff_ID==Stone_Blue || cp_eff_ID==Stone_Black) // White, Red, Green, Blue, or Black Stone
        return true;                                                                                                                 // For all weapons and classes

    if (cp_eff_ID==Crystal_Red || cp_eff_ID == Crystal_Yellow){                                     // Red or Yellow Crystal
        if (class_ID==1 || class_ID==2 || class_ID==3 || class_ID==6 || class_ID==7 || class_ID==8) // Only for Boxer (1), Gladiator (2), Sniper (3), Gunner (6), Whipper (7), and Angel (8)
            return true;
    }
    if (cp_eff_ID==Crystal_Silver || cp_eff_ID==Crystal_Purple || cp_eff_ID==Crystal_Black) // Silver, Purple, or Black Crystal
        return true;                                                                        // For all weapons and classes

    if (cp_eff_ID==Jewel_Ruby || cp_eff_ID==Jewel_Garnet){ // Ruby or Garnet
        if (weap_type==1)                                  // Only for fire (type 1) weapons
            return true;
    }
    if (cp_eff_ID==Jewel_Sapphire || cp_eff_ID==Jewel_Aquamarine){ // Sapphire or Aquamarine
        if (weap_type==2)                                          // Only for ice (type 2) weapons
            return true;
    }
    if (cp_eff_ID==Jewel_Topaz){ // Topaz
        if (weap_type==3)        // Only for thunder (type 3) weapons
            return true;
    }
    if (cp_eff_ID==Jewel_Emerald || cp_eff_ID==Jewel_Peridot){ // Emerald or Peridot
        if (weap_type==4)                                      // Only for poison (type 4) weapons
            return true;
    }
    if (cp_eff_ID==Jewel_Diamond){ // Diamond
        if (weap_type==5)          // Only for freeze (type 5) weapons
            return true;
    }
    if (cp_eff_ID==Card_Quicks) // Quick's Card
        return true;            // For all weapons and classes

    if (cp_eff_ID==Card_Longsw){ // Long Sword's Card
        if (class_ID==2)         // Only works for Gladiator (2)
            return true;
    }
    if (cp_eff_ID==Card_Catapt){                                                     // Catapult's Card
        if (class_ID==3 || class_ID==4 || class_ID==5 || class_ID==6 || class_ID==8) // Only works for Sniper (3), Magician (4), Priest (5), Gunner (6), and Angel (8)
            return true;
    }
    if (cp_eff_ID==Card_Pierce){                       // Pierce's Card
        if (class_ID==3 || class_ID==4 || class_ID==6) // Only works for Sniper (3), Magician (4), and Gunner (6)
            return true;
    }
    if (cp_eff_ID==Card_Guides){                       // Guide's Card
        if (class_ID==3 || class_ID==4 || class_ID==6) // Only works for Sniper (3), Magician (4), and Gunner (6)
            return true;
    }
    if (cp_eff_ID==Card_Bullet){ // Bullet's Card
        if (weap_bullet>=2)      // Only works if weapon has 2 or more bullets
            return true;
    }
    if (cp_eff_ID==Card_Explsn){                                      // Explosion's Card
        if (class_ID==1 || class_ID==3 || class_ID==6 || class_ID==8) // Only for Boxer (1), Sniper (3), Gunner (6), and Angel (8)
            return true;
    }
    if (cp_eff_ID==Card_Bersrk){                       // Berserk Card
        if (class_ID==1 || class_ID==2 || class_ID==7) // Only for Boxer (1), Gladiator (2), and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Critcl){                                                                    // Critical's Card
        if (class_ID==1 || class_ID==2 || class_ID==3 || class_ID==6 || class_ID==7 || class_ID==8) // Only for Boxer (1), Gladiator (2), Sniper (3), Gunner (6), Whipper (7), and Angel (8)
            return true;
    }
    if (cp_eff_ID==Card_Big){           // Big Card
        if (class_ID==1 || class_ID==7) // Only for Boxer (1) and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Knockb){                                                     // Knockback's Card
        if (class_ID==1 || class_ID==3 || class_ID==4 || class_ID==6 || class_ID==7) // Only for Boxer (1), Sniper (3), Magician (4), Gunner (6), and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Reflct){                                                     // Reflection Card
        if (class_ID==3 || class_ID==4 || class_ID==6 || class_ID==7 || class_ID==8) // Only for Sniper (3), Magician (4), Gunner (6), Whipper (7), and Angel (8)
            return true;
    }
    if (cp_eff_ID==Card_Rings){ // Ring's Card
        if (class_ID==8)        // Only for Angel (8)
            return true;
    }
    if (cp_eff_ID==Card_Vampir){                       // Vampire's Card
        if (class_ID==1 || class_ID==2 || class_ID==7) // Only for Boxer (1), Gladiator (2), and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Heals) // Heal's Card
        return true;           // For all weapons and classes

    if (cp_eff_ID==Card_Katana){ // Katana's Card
        if (class_ID==2)         // Only for Gladiator (8)
            return true;
    }
    if (cp_eff_ID==Card_ONIGIR){                       // ONIGIRI's Card
        if (class_ID==1 || class_ID==2 || class_ID==7) // Only for Boxer (1), Gladiator (2), and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Gldrsh){                       // Gold rush Card
        if (class_ID==1 || class_ID==2 || class_ID==7) // Only for Boxer (1), Gladiator (2), and Whipper (7)
            return true;
    }
    if (cp_eff_ID==Card_Zombie) // Zombie's Card
        return true;            // For all weapons and classes

    if (cp_eff_ID==Medal_Bronze || cp_eff_ID==Medal_Silver || cp_eff_ID==Medal_Gold || cp_eff_ID==Medal_Iron) // Bronze, Silver, Gold, or Iron Medal
        return true;                                                                                          // For all weapons and classes

    if (cp_eff_ID==Charm_Ice || cp_eff_ID==Charm_Poison || cp_eff_ID==Charm_Freeze) // Ice, Poison, or Freeze Charm
        return true;                                                                // For all weapons and classes

    if (cp_eff_ID==Spirit_Eff) // Any Spirit
        return true;           // For all weapons and classes

    if (cp_eff_ID==Crown_Imprl || cp_eff_ID==Crown_Anger) // Imperial or Anger Crown
        return true;                                      // For all weapons and classes

    return false;
}

var DPSM_Calculator = new SR_calculator;
function SR_calculator(){ // new function
    this.CL_dmg_session = Array((Stickmen_Slots<<1)); // damage done during current session
    this.CL_DPS_session = Array((Stickmen_Slots<<1)); // DPS done during current session
    this.CL_dmg_stage = Array((Stickmen_Slots<<1));   // damage done during current stage
    this.CL_DPS_stage = Array((Stickmen_Slots<<1));   // DPS done during current stage
    this.CL_interval_count = 5;                       // number of intervals
    this.CL_intervals = [6,15,30,60,120];             // list of intervals
    this.CL_dmg = Array((Stickmen_Slots<<1));         // damage dealt, resets after each interval
    this.CL_dmg_intv = Array((Stickmen_Slots<<1));    // damage dealt during the previous interval
    this.CL_DPS_avg = Array((Stickmen_Slots<<1));     // average damage per active interval
    this.CL_dmg_sum = Array((Stickmen_Slots<<1));     // sum of damage during all active intervals
    this.CL_intv_count = Array((Stickmen_Slots<<1));  // count of active intervals
    this.CL_time_screen = 0;                          // time when screen started
    this.CL_time_stage = 0;                           // time when stage started
    for (var s=0; s<Stickmen_Slots<<1; s++){
        this.CL_dmg[s] = Array(this.CL_interval_count);
        this.CL_dmg_intv[s] = Array(this.CL_interval_count);
        this.CL_DPS_avg[s] = Array(this.CL_interval_count);
        this.CL_dmg_sum[s] = Array(this.CL_interval_count);
        this.CL_intv_count[s] = Array(this.CL_interval_count);
    }
}

// reset stats
/*
0: session
1: stage
2: screen
*/
SR_calculator.prototype.CLreset = function(scope){
    for (var s=0; s<Stickmen_Slots<<1; s++){
        switch (scope){
            case 0:
                this.CL_dmg_session[s] = 0;
                this.CL_DPS_session[s] = 0;
            case 1:
                this.CL_time_stage = 0;
                this.CL_dmg_stage[s] = 0;
                this.CL_DPS_stage[s] = 0;
                for (var i=0; i<this.CL_interval_count; i++){
                    this.CL_dmg[i][s] = 0;
                    this.CL_dmg_intv[i][s] = 0;
                    this.CL_DPS_avg[i][s] = 0;
                    this.CL_dmg_sum[i][s] = 0;
                    this.CL_intv_count[i][s] = 0;
                }
            case 2:
                this.CL_time_screen = 0;
                break;
        }
    }
}

// divide but avoid divide by 0 error
SR_calculator.prototype.CLdivide = function(numerator,denominator){
    if (denominator==0)
        return 0;
    else
        return Math.round(numerator/denominator);
}

// seperate into equal segments
SR_calculator.prototype.CLsegmentBar = function(bar_left,bar_top,bar_width,bar_height,numerator,denominator){
    var segment_width = 0;
    var segment_left = bar_left;
    var longest_segment = 0;
    var segment = Array(Stickmen_Slots);
    for (var s=0; s<Stickmen_Slots; s++)
        segment[s] = Array(2);

    filledRect(bar_left,bar_top-1,bar_width,bar_height+2,0x000000); // bar background

    for (var s=0; s<Stickmen_Slots; s++){
        segment_left += segment_width+1; // adjust segment over by the previous segment's width and 1 space for seperation
        segment[s][0] = segment_left;
        if (denominator==0)
            segment_width = bar_width-5>>2; // seperate into 4 equal parts if the total sum is 0
        else
            segment_width = maxOf(floor(numerator[s]/denominator*(bar_width-5)),1);
        segment[s][1] = floor(segment_width);

        if (segment[s][1] > segment[longest_segment][1])
            longest_segment = s;
    }

    while (segment[0][1]+segment[1][1]+segment[2][1]+segment[3][1] < bar_width-5){ // if the segment total sum exceeds the width
        segment[longest_segment][1]++;                                         // adjust longest segment
        for (var s=longest_segment+1; s<Stickmen_Slots; s++)
            segment[s][0]++;                                                   // shift the rest of the segments
    }
    while (segment[0][1]+segment[1][1]+segment[2][1]+segment[3][1] > bar_width-5){ // if the segment total sum is lower than the width
        segment[longest_segment][1]--;                                         // adjust longest segment
        for (var s=longest_segment+1; s<Stickmen_Slots; s++)
            segment[s][0]--;                                                   // shift the rest of the segments
    }

    for (var s=0; s<Stickmen_Slots; s++)
        filledRect(segment[s][0],bar_top,segment[s][1],bar_height,getVal(Item_Inv[Stickmen_Slots+s],Item_Color)); // dmg comparison segments
}

