    this.EN_array_ID[a] = this.EN_array_ID[this.EN_index_current-1];
    this.EN_species_ID[a] = this.EN_species_ID[this.EN_index_current-1];
    this.EN_state[a] = this.EN_state[this.EN_index_current-1];
