function normalize(vec1){ // original name: Xg()
    var magnitude1 = magnitudeOf(vec1);

    if (magnitude1==0)
        return 0;

    vec1.x = vec1.x/magnitude1;
    vec1.y = vec1.y/magnitude1;

    return magnitude1;
}

function angleToXAxis(vec1){ // original name: Vg()
    var b = Math.acos(vec1.x/Math.sqrt(vec1.x*vec1.x+vec1.y*vec1.y));

    if (vec1.y > 0)
        b = TwoPi-b;

    return b;
}

var Rand_Arr_Df = new Float32Array(1024); // original name: df
var Rand_EF = 0; // original name: ef
var Rand_FF = 0; // original name: ff
function random(num1){ // original name: A()
    Rand_EF += Rand_FF;
    Rand_EF &= 1023;
    return Rand_Arr_Df[Rand_EF]*num1;
}

function randomRange(minimum,maximum){ // original name: N()
    Rand_EF = Rand_EF+Rand_FF;
    Rand_EF &= 1023;
    return Rand_Arr_Df[Rand_EF]*(maximum-minimum)+minimum;
}

function fiftyfifty(num1,num2){ // original name: Kf()
    Rand_EF = Rand_EF+Rand_FF;
    Rand_EF &= 1023;

    if (Rand_Arr_Df[Rand_EF]<0.5)
         return num1;
    else return num2;
}

function randInt(num1){ // original name: $g()
    Rand_EF += Rand_FF;
    Rand_EF &= 1023;
    return ~~(Rand_Arr_Df[Rand_EF]*num1); // ~~() is floor()
}

var Xe_arr = Array(513);   // original name: Xe
var Pi = 3.1415927;    // original name: Ye
var TwoPi = 6.2831855; // original name: Wg

function absVal(num1){ // original name: Kg()
    if (num1 < 0)
         return -num1;
    else return num1;
}

// returns the higher value (return 2nd if they are equal) original name: ig()
function maxOf(num1,num2){
    if (num1 > num2)
         return num1;
    else return num2;
}

// returns the lower value (return 2nd if they are equal) new function
function minOf(num1,num2){
    if (num1 < num2)
         return num1;
    else return num2;
}

// keeps value within parameters original name: K()
function clamp(value,minimum,maximum){
    if (value < minimum)
         return minimum;
    else if (value > maximum)
         return maximum;
    else return value;
}

// used to cycle when scrolling through settings and lists original name: Zf()
function cycle(next,first,last){
    if (next < first)
         return last;
    else if (next > last)
         return first;
    else return next;
}

// truncates decimals original name: z()
function floor(number){
    return Math.floor(number);
};

// gets the first stickman on the team (PvE team or PvP team)
function getLeader(stickman_ID,team){
    var leader;
    if (team==0) // if looking for friendly team leader
        leader = floor(stickman_ID/Stickmen_Slots)*Stickmen_Slots;
    if (team==1) // if looking for opponent team leader
        leader = (1-floor(stickman_ID/Stickmen_Slots))*Stickmen_Slots;

    return leader;
}

// CHANGE THE INITIAL SEED HERE (mostly possible seeds: 3,6,8)
Math.seed = 11;

Math.seededRandom = function(maximum,minimum) {
    maximum = maximum || 1;
    minimum = minimum || 0;

    Math.seed = (Math.seed*9301+49297)%233280;
    var rnd = Math.seed/233280.0;

    return minimum+rnd*(maximum-minimum);
}

function shuffle(array_var) {
  var current_index = array_var.length;
  var temporary_value,random_index;

  // While there remain elements to shuffle...
  while (current_index!=0) {

    // Pick a remaining element...
    random_index = Math.floor(Math.seededRandom()*current_index);
    current_index -= 1;

    // And swap it with the current element.
    temporary_value = array_var[current_index];
    array_var[current_index] = array_var[random_index];
    array_var[random_index] = temporary_value;
  }

  return array_var;
}

var Beaten2 = 2;
var Randomizer_Mode = 0;
//Randomize(); // Randomizer (based on a mod by Aho)
function Randomize(){
    Beaten2 = 1; // allow user to buy books for unlocked or beaten stages
    Randomizer_Mode = 1;
    var new_book_indexer = [0,0,5,9,14,19,23,27,31,35,39,40,44,48,52,56,60,64,69,74,79,79,84,89,93,97,101,105,109,113,114,118,122,126,128,132,136,140,144,148,152,156,160,161,165,169,173,177,177,182,187,192,196,200,204,208,212,216,220,224,228,232,236,240,241,245,249,253,256,260,264,264,266,270,274,278,282,286,286,290,294,298,302,306,311,316,320,324,328,329,339]; // original name Pe[]
    var enemy_drops = [];
    var randomized_EN_Info = [];
    var extra_attacks = [];
    var en_info_length = EN_Info.length;
    var forest_4_slot = 0;
    var m,b,current_stage,forest_4_spawner;

    // copy drops and bookmark drop slots
    for (var i=0; i<en_info_length-1; i++){
        for (var d=En_Drop1; d<=En_Drop1+4; d+=2){
            if (EN_Info[i][d]!=0 && EN_Info[i][d]!=2){ // skip empty slots and onigiri
                enemy_drops.push(EN_Info[i][d]);
                EN_Info[i][d] = -1;
            }
        }
    }
    shuffle(enemy_drops);
    // re-add drops after shuffling
    for (var i=0; i<en_info_length-1; i++){
        for (var d=En_Drop1; d<=En_Drop1+4; d+=2){
            if (EN_Info[i][d]==-1){
                EN_Info[i][d] = enemy_drops[0];
                enemy_drops.shift();
            }
        }
    }
    // copy enemy arrays
    for (var i=5; i<en_info_length-1; i++){
        if (EN_Info[i][0]!=0)
             randomized_EN_Info.push(EN_Info[i]);
        else extra_attacks.push(EN_Info[i]);
    }
    shuffle(randomized_EN_Info);
    shuffle(extra_attacks);
    // re-add enemy arrays after shuffling, also adjust Book_Indexer to account for secondary attacks
    current_stage = 0;
    for (var i=5; i<en_info_length-1; i++){
        while (new_book_indexer[current_stage+1]<=i)
            current_stage++;
        if (randomized_EN_Info[0][7]==7 || randomized_EN_Info[0][EN_Species]==15 && randomized_EN_Info[0][EN_Size]>1){ // isolate and save the first spawner, it will be used in forest 4
             forest_4_spawner = randomized_EN_Info[0];
             randomized_EN_Info.shift();
        } else {
            EN_Info[i] = randomized_EN_Info[0];
            if (randomized_EN_Info[0][En_2nd_Att] > 0){                   // if enemy has an extra attack
                for (var a=1; a<=randomized_EN_Info[0][En_2nd_Att]; a++){ //  for each extra attack that the enemy should have
                    EN_Info[i+a] = extra_attacks[0];                      //   and add random extra attack
                    extra_attacks.shift();
                }
                for (var b=current_stage+1; b<new_book_indexer.length-1; b++)
                    new_book_indexer[b] += randomized_EN_Info[0][En_2nd_Att];
                i += randomized_EN_Info[0][En_2nd_Att];
            }
            randomized_EN_Info.shift();
        }
        if (current_stage==67 && forest_4_slot==0)
            forest_4_slot = ++i;
    }
    if (forest_4_spawner[EN_Species]==15)
        forest_4_spawner[26]==-1;
    EN_Info[forest_4_slot] = forest_4_spawner;

    Book_Indexer = new_book_indexer;
    // adjust enemy spawns to account for secondary attacks
    m = 0;
    for (var t=0; t<Stage_Spawns.length; t++){
        if (t==11) m++;    // adjust after castle
        if (t==30) m++;    // adjust after submarine shrine
        if (t==43) m++;    // adjust after pyramid
        if (t==64) m++;    // adjust after ice castle
        if (t==89) m += 5; // adjust after hell castle
        for (var s=0; s<Stage_Spawns[t].length; s++){
            for (var g=3; g<Stage_Spawns[t][s].length; g+=3){
                b = 0;
                for (var e=0; e<Stage_Spawns[t][s][g]+b-m; e++)
                    b += EN_Info[e][En_2nd_Att];
                while (EN_Info[Stage_Spawns[t][s][g]+b-m][0]==0) b--; // avoid spawning 2nd attacks
                Stage_Spawns[t][s][g] += b-m;
            }
        }
    }
}